import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-marvel-snap-deck-randomiser',
  templateUrl: './marvel-snap-deck-randomiser.component.html'
})
export class MarvelSnapDeckRandomiserComponent {
  public deck: Deck = {} as Deck;

  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    http.get<Deck>(baseUrl + 'deck').subscribe(result => {
      this.deck = result;
    }, error => console.error(error));
  }
}

interface Deck {
  name: string;
  energy: number;
  power: number;
  picUrl: string;
}
